DELIMITER $$
CREATE TRIGGER `tr_validate_approval_timing` BEFORE UPDATE ON `approvals` FOR EACH ROW BEGIN
    IF NEW.approval_decision_at IS NOT NULL AND NEW.approval_decision_at < OLD.submitted_at THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'Approval decision cannot be before submission time';
    END IF;
END
$$
DELIMITER ;
DELIMITER $$
CREATE TRIGGER `tr_validate_approver_authorization` BEFORE INSERT ON `approvals` FOR EACH ROW BEGIN
    DECLARE user_role_val ENUM('CEO','SYSTEM_ADMIN','STATION_MANAGER','DELIVERY_SUPERVISOR','AUDITOR','STOCK_KEEPER');

    SELECT role INTO user_role_val
    FROM users
    WHERE id = NEW.approver_user_id;

    IF NOT fn_validate_approver_role(NEW.approval_level, user_role_val) THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'User role does not have authorization for this approval level';
    END IF;
END
$$
DELIMITER ;

