--
-- Table structure for table `batch_consumption`
--

CREATE TABLE `batch_consumption` (
  `id` bigint UNSIGNED NOT NULL,
  `tank_inventory_layer_id` bigint UNSIGNED NOT NULL,
  `reading_id` bigint UNSIGNED NOT NULL,
  `pump_id` bigint UNSIGNED DEFAULT NULL,
  `sale_date` date NOT NULL,
  `consumption_sequence` smallint UNSIGNED NOT NULL,
  `quantity_consumed_liters` decimal(12,3) NOT NULL,
  `cost_per_liter` decimal(10,4) NOT NULL,
  `total_cost_consumed` decimal(15,2) NOT NULL,
  `layer_balance_before_liters` decimal(12,3) NOT NULL,
  `layer_balance_after_liters` decimal(12,3) NOT NULL,
  `is_layer_depleted` tinyint(1) DEFAULT '0',
  `consumption_method` enum('FIFO_AUTO','MANUAL_ALLOCATION','PROPORTIONAL') COLLATE utf8mb4_unicode_ci DEFAULT 'FIFO_AUTO',
  `consumption_timestamp` timestamp NULL DEFAULT CURRENT_TIMESTAMP
) ;

--
-- Triggers `batch_consumption`
--
DELIMITER $$
CREATE TRIGGER `tr_validate_consumption_quantities` BEFORE INSERT ON `batch_consumption` FOR EACH ROW BEGIN
    IF NEW.quantity_consumed_liters <= 0 THEN
        INSERT INTO system_health_monitoring (check_type, check_status, check_details, severity)
        VALUES ('VALIDATION_ERROR', 'FAILED',
               CONCAT('Invalid consumption quantity: ', NEW.quantity_consumed_liters, 'L'), 'HIGH');

        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'Consumption quantity must be greater than zero';
    END IF;
END
$$
DELIMITER ;

