DELIMITER $$
CREATE TRIGGER `tr_deliveries_hash_chain` BEFORE INSERT ON `deliveries` FOR EACH ROW BEGIN
    DECLARE prev_hash VARCHAR(64);

    SELECT hash_current INTO prev_hash
    FROM deliveries
    WHERE tank_id = NEW.tank_id
    ORDER BY created_at DESC
    LIMIT 1;

    SET NEW.hash_previous = prev_hash;
    SET NEW.hash_current = SHA2(CONCAT(
        NEW.tank_id,
        NEW.delivery_date,
        NEW.quantity_delivered_liters,
        NEW.cost_per_liter,
        NEW.received_by,
        IFNULL(prev_hash, ''),
        UNIX_TIMESTAMP(NOW(6))
    ), 256);
END
