DELIMITER $$
CREATE TRIGGER `tr_enhanced_delivery_fifo_layers` AFTER UPDATE ON `deliveries` FOR EACH ROW BEGIN
    DECLARE next_sequence SMALLINT UNSIGNED;
    DECLARE layer_exists INT DEFAULT 0;
    DECLARE processing_enabled TINYINT(1) DEFAULT 1;

    -- Check if delivery layer creation is enabled
    SELECT COALESCE(config_value_boolean, 1) INTO processing_enabled
    FROM system_configurations
    WHERE config_key = 'AUTO_DELIVERY_LAYER_CREATION'
    LIMIT 1;

    -- Only trigger on status change to COMPLETED and if processing enabled
    IF NEW.delivery_status = 'COMPLETED' AND
       (OLD.delivery_status != 'COMPLETED' OR OLD.delivery_status IS NULL) AND
       processing_enabled = 1 THEN

        -- Check if FIFO layer already exists for this delivery
        SELECT COUNT(*) INTO layer_exists
        FROM tank_inventory_layers
        WHERE delivery_id = NEW.id;

        -- Only create if layer doesn't exist
        IF layer_exists = 0 THEN

            -- Get next sequence number for this tank
            SELECT COALESCE(MAX(layer_sequence_number), 0) + 1 INTO next_sequence
            FROM tank_inventory_layers
            WHERE tank_id = NEW.tank_id;

            -- Create enhanced FIFO layer
            INSERT INTO tank_inventory_layers (
                tank_id, delivery_id, layer_sequence_number,
                delivery_batch_number, opening_quantity_liters,
                current_quantity_liters, consumed_quantity_liters,
                cost_per_liter, total_layer_cost, remaining_layer_value,
                delivery_temperature_celsius, delivery_density,
                layer_created_at, is_depleted, layer_status
            ) VALUES (
                NEW.tank_id, NEW.id, next_sequence,
                CONCAT('ENHANCED-BATCH-', NEW.id, '-', DATE_FORMAT(NOW(), '%Y%m%d')),
                NEW.quantity_delivered_liters, NEW.quantity_delivered_liters, 0.000,
                NEW.cost_per_liter,
                ROUND(NEW.quantity_delivered_liters * NEW.cost_per_liter, 2),
                ROUND(NEW.quantity_delivered_liters * NEW.cost_per_liter, 2),
                COALESCE(NEW.delivery_temperature_celsius, 25.0),
                COALESCE(NEW.density_at_15c, 0.8500),
                NOW(), 0, 'ACTIVE'
            );

            -- Log successful creation
            INSERT INTO system_health_monitoring (check_type, check_status, check_details, affected_records, severity)
            VALUES ('ENHANCED_LAYER_CREATION', 'PASSED',
                   CONCAT('Enhanced FIFO layer created: ', NEW.quantity_delivered_liters, 'L for delivery ', NEW.id,
                         ' in tank ', NEW.tank_id, ' (sequence: ', next_sequence, ')'), 1, 'LOW');

        ELSE

            -- Log that layer already exists
            INSERT INTO system_health_monitoring (check_type, check_status, check_details, severity)
            VALUES ('LAYER_CREATION_SKIPPED', 'PASSED',
                   CONCAT('FIFO layer already exists for delivery ', NEW.id), 'LOW');

        END IF;

    END IF;

END
$$
