CREATE TRIGGER `tr_validate_tank_capacity` BEFORE INSERT ON `deliveries` FOR EACH ROW BEGIN
    DECLARE tank_capacity DECIMAL(12,3);
    DECLARE current_stock DECIMAL(12,3) DEFAULT 0;
    DECLARE error_message VARCHAR(500);

    -- Get tank capacity
    SELECT capacity_liters INTO tank_capacity
    FROM tanks
    WHERE id = NEW.tank_id;

    -- Get latest dip reading for this tank (most recent)
    SELECT IFNULL(dip_reading_liters, 0) INTO current_stock
    FROM readings
    WHERE tank_id = NEW.tank_id
      AND dip_reading_liters IS NOT NULL
    ORDER BY reading_date DESC, reading_time DESC
    LIMIT 1;

    -- Check if delivery would exceed capacity
    IF (current_stock + NEW.quantity_delivered_liters) > tank_capacity THEN
        -- Build error message using separate variables (MySQL CONCAT limitation in SIGNAL)
        SET error_message = CONCAT(
            'DELIVERY REJECTED: Would exceed tank capacity. Current: ',
            CAST(current_stock AS CHAR),
            'L + Delivery: ',
            CAST(NEW.quantity_delivered_liters AS CHAR),
            'L > Capacity: ',
            CAST(tank_capacity AS CHAR),
            'L'
        );

        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = error_message;
    END IF;
END
