DELIMITER $$
CREATE TRIGGER `tr_enhanced_meter_fifo_automation` AFTER INSERT ON `meter_readings` FOR EACH ROW BEGIN
    DECLARE calculated_sales DECIMAL(12,3);
    DECLARE tank_id_val BIGINT UNSIGNED;
    DECLARE last_reading DECIMAL(15,3);
    DECLARE reading_record_id BIGINT UNSIGNED;
    DECLARE processing_enabled TINYINT(1) DEFAULT 1;
    DECLARE station_id_val BIGINT UNSIGNED;
    DECLARE product_type_val ENUM('PETROL_95','PETROL_98','DIESEL','KEROSENE','JET_A1','HEAVY_FUEL_OIL','LIGHT_FUEL_OIL','LPG_AUTOGAS','ETHANOL_E10','ETHANOL_E85','BIODIESEL_B7','BIODIESEL_B20');
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
        INSERT INTO system_health_monitoring (check_type, check_status, check_details, severity)
        VALUES ('TRIGGER_ERROR', 'FAILED',
               CONCAT('tr_enhanced_meter_fifo_automation failed for pump ', NEW.pump_id), 'HIGH');
    END;

    -- Check if processing is enabled
    SELECT COALESCE(config_value_boolean, 1) INTO processing_enabled
    FROM system_configurations
    WHERE config_key = 'ENHANCED_FIFO_PROCESSING_ENABLED'
    LIMIT 1;

    IF processing_enabled = 1 THEN

        -- Get tank ID for this pump
        SELECT tank_id INTO tank_id_val
        FROM pumps
        WHERE id = NEW.pump_id AND is_operational = 1;

        IF tank_id_val IS NOT NULL THEN

            -- Get station_id and product_type in one query
            SELECT p.station_id, COALESCE(pr.product_type, 'DIESEL')
            INTO station_id_val, product_type_val
            FROM pumps p
            LEFT JOIN tanks t ON p.tank_id = t.id
            LEFT JOIN products pr ON t.product_id = pr.id
            WHERE p.id = NEW.pump_id;

            -- Get previous meter reading
            SELECT meter_reading_liters INTO last_reading
            FROM meter_readings
            WHERE pump_id = NEW.pump_id
            AND id != NEW.id
            ORDER BY reading_timestamp DESC, id DESC
            LIMIT 1;

            -- Calculate sales volume
            IF NEW.meter_reset_occurred = 1 THEN
                SET calculated_sales = NEW.meter_reading_liters +
                    COALESCE(NEW.pre_reset_reading - COALESCE(last_reading, 0), 0);
            ELSE
                SET calculated_sales = NEW.meter_reading_liters - COALESCE(last_reading, 0);
            END IF;

            IF calculated_sales > 0.001 THEN

                -- Create readings table entry for audit trail
                INSERT INTO readings (
                    station_id, reading_type, reading_date, reading_time, reading_shift,
                    tank_id, pump_id, product_type, meter_reading_liters,
                    calculated_sales_liters, reading_status, entry_method, entry_device,
                    reading_confidence_level, environmental_conditions, hash_current,
                    entered_by, created_at, updated_at
                ) VALUES (
                    station_id_val, 'ENHANCED_METER_AUTO', NEW.reading_date, TIME(NEW.reading_timestamp), NEW.reading_shift,
                    tank_id_val, NEW.pump_id, product_type_val, NEW.meter_reading_liters,
                    calculated_sales, 'AUTOMATED', 'ENHANCED_TRIGGER', 'SYSTEM',
                    'HIGH', 'NORMAL', SHA2(CONCAT(NEW.id, NOW(), RAND()), 256),
                    NEW.entered_by, NOW(), NOW()
                );

                SET reading_record_id = LAST_INSERT_ID();

                -- Log successful processing with CORRECT enum value
                INSERT INTO system_health_monitoring (check_type, check_status, check_details, severity)
                VALUES ('FIFO_PROCESSING_QUEUED', 'PASSED',
                       CONCAT('Sales: ', calculated_sales, 'L, Tank: ', tank_id_val, ', Reading: ', reading_record_id), 'LOW');

            ELSEIF calculated_sales < -0.001 THEN

                -- Log negative sales for investigation
                INSERT INTO system_health_monitoring (check_type, check_status, check_details, severity)
                VALUES ('NEGATIVE_SALES_DETECTED', 'WARNING',
                       CONCAT('Negative sales: ', calculated_sales, 'L for pump ', NEW.pump_id,
                             ' (Tank: ', tank_id_val, ')'), 'MEDIUM');

            END IF;

        ELSE

            -- Log pump/tank mapping error
            INSERT INTO system_health_monitoring (check_type, check_status, check_details, severity)
            VALUES ('PUMP_TANK_MAPPING_ERROR', 'WARNING',
                   CONCAT('No active tank found for pump ', NEW.pump_id), 'MEDIUM');

        END IF;

    END IF;

END
$$
