CREATE TRIGGER `tr_validate_meter_progression` BEFORE INSERT ON `meter_readings` FOR EACH ROW BEGIN
    DECLARE last_reading DECIMAL(15,3);
    DECLARE reset_threshold DECIMAL(15,3);

    -- Get the pump's reset threshold
    SELECT meter_reset_threshold INTO reset_threshold
    FROM pumps
    WHERE id = NEW.pump_id;

    -- Get last reading for this pump
    SELECT meter_reading_liters INTO last_reading
    FROM meter_readings
    WHERE pump_id = NEW.pump_id
    ORDER BY reading_date DESC, reading_shift DESC
    LIMIT 1;

    -- Check if meter went backward (unless it's a legitimate reset)
    IF last_reading IS NOT NULL THEN
        IF NEW.meter_reading_liters < last_reading THEN
            -- Check if this could be a meter reset
            IF last_reading >= (reset_threshold * 0.95) AND NEW.meter_reading_liters < (reset_threshold * 0.1) THEN
                -- Legitimate meter reset - mark it
                SET NEW.meter_reset_occurred = 1;
                SET NEW.pre_reset_reading = last_reading;
            ELSE
                -- Fraud alert - meter going backward without reset
                SIGNAL SQLSTATE '45000'
                SET MESSAGE_TEXT = 'FRAUD ALERT: Meter reading cannot go backward without reset';
            END IF;
        END IF;
    END IF;
END
$$
