DELIMITER $$
CREATE TRIGGER `tr_auto_variance_detection` AFTER INSERT ON `readings` FOR EACH ROW BEGIN
    DECLARE variance_pct DECIMAL(6,3);
    DECLARE variance_liters DECIMAL(12,3);
    DECLARE threshold_minor DECIMAL(6,3) DEFAULT 0.5;
    DECLARE threshold_moderate DECIMAL(6,3) DEFAULT 1.0;
    DECLARE threshold_significant DECIMAL(6,3) DEFAULT 2.0;
    DECLARE threshold_critical DECIMAL(6,3) DEFAULT 5.0;
    DECLARE reconciliation_id BIGINT UNSIGNED DEFAULT NULL;

    -- Calculate variance if we have the data
    IF NEW.variance_from_expected_percentage IS NOT NULL THEN
        SET variance_pct = ABS(NEW.variance_from_expected_percentage);
        SET variance_liters = ABS(IFNULL(NEW.variance_from_expected_liters, 0));

        -- Only create variance record if above minimum threshold
        IF variance_pct > threshold_minor OR variance_liters > 50 THEN

            -- Try to get the daily reconciliation ID (may not exist yet)
            SELECT id INTO reconciliation_id
            FROM daily_reconciliations
            WHERE station_id = NEW.station_id
              AND tank_id = NEW.tank_id
              AND reconciliation_date = NEW.reading_date
            LIMIT 1;

            -- Create variance record with ALL required fields
            INSERT INTO variances (
                reading_id,
                daily_reconciliation_id,
                station_id,
                tank_id,
                product_type,
                variance_type,
                variance_category,
                variance_source,
                calculated_variance_liters,
                calculated_variance_percentage,
                variance_direction,
                financial_impact_cost,
                financial_impact_revenue,
                financial_impact_net,
                cumulative_variance_7_days,
                cumulative_variance_30_days,
                variance_frequency_score,
                statistical_significance,
                pattern_detected,
                pattern_type,
                similar_variances_count,
                historical_pattern_match,
                risk_level,
                theft_probability_score,
                equipment_fault_probability,
                measurement_error_probability,
                environmental_factor_probability,
                human_error_probability,
                recommended_action,
                investigation_priority,
                variance_status,
                escalation_level,
                calculation_method,
                created_at,
                variance_liters,
                variance_percentage
            ) VALUES (
                NEW.id,
                reconciliation_id,
                NEW.station_id,
                NEW.tank_id,
                NEW.product_type,
                'STOCK_DISCREPANCY',
                CASE
                    WHEN variance_pct <= threshold_minor THEN 'MINOR'
                    WHEN variance_pct <= threshold_moderate THEN 'MODERATE'
                    WHEN variance_pct <= threshold_significant THEN 'SIGNIFICANT'
                    ELSE 'CRITICAL'
                END,
                'AUTO_DETECTION',
                variance_liters,
                variance_pct,
                CASE WHEN NEW.variance_from_expected_liters > 0 THEN 'POSITIVE' ELSE 'NEGATIVE' END,
                variance_liters * 3000,  -- financial_impact_cost
                0.00,  -- financial_impact_revenue (MISSING FIELD - ADDED)
                variance_liters * 3000,  -- financial_impact_net
                0.000,  -- cumulative_variance_7_days
                0.000,  -- cumulative_variance_30_days
                0.00,   -- variance_frequency_score
                'MEDIUM',  -- statistical_significance
                0,      -- pattern_detected
                'NONE', -- pattern_type
                0,      -- similar_variances_count
                0,      -- historical_pattern_match
                CASE
                    WHEN variance_pct <= threshold_minor THEN 'LOW'
                    WHEN variance_pct <= threshold_moderate THEN 'MEDIUM'
                    WHEN variance_pct <= threshold_significant THEN 'HIGH'
                    ELSE 'CRITICAL'
                END,
                0.00,   -- theft_probability_score
                0.00,   -- equipment_fault_probability
                50.00,  -- measurement_error_probability
                0.00,   -- environmental_factor_probability
                50.00,  -- human_error_probability
                'INVESTIGATE',
                CASE
                    WHEN variance_pct <= threshold_minor THEN 'LOW'
                    WHEN variance_pct <= threshold_moderate THEN 'MEDIUM'
                    WHEN variance_pct <= threshold_significant THEN 'HIGH'
                    ELSE 'URGENT'
                END,
                'PENDING',
                CASE
                    WHEN variance_pct <= threshold_minor THEN 'STATION'
                    WHEN variance_pct <= threshold_moderate THEN 'REGIONAL'
                    WHEN variance_pct <= threshold_significant THEN 'CEO'
                    ELSE 'AUDIT'
                END,
                'AUTO_TRIGGER_DETECTION',
                NOW(),
                variance_liters,
                variance_pct
            );

        END IF;
    END IF;
END
$$
DELIMITER ;
DELIMITER $$
CREATE TRIGGER `tr_readings_hash_chain` BEFORE INSERT ON `readings` FOR EACH ROW BEGIN
    DECLARE prev_hash VARCHAR(64);

    -- Get the last hash from this station's chain
    SELECT hash_current INTO prev_hash
    FROM readings
    WHERE station_id = NEW.station_id
    ORDER BY created_at DESC
    LIMIT 1;

    SET NEW.hash_previous = prev_hash;
    SET NEW.hash_current = SHA2(CONCAT(
        NEW.station_id,
        NEW.reading_date,
        IFNULL(NEW.dip_reading_liters, 0),
        IFNULL(NEW.meter_reading_liters, 0),
        NEW.entered_by,
        IFNULL(prev_hash, ''),
        UNIX_TIMESTAMP(NOW(6))
    ), 256);

    -- Set validation status
    SET NEW.hash_validation_status = 'VALID';
END
$$
DELIMITER ;
DELIMITER $$
CREATE TRIGGER `tr_validate_future_dates` BEFORE INSERT ON `readings` FOR EACH ROW BEGIN
    -- Prevent readings dated in the future (more than 1 day ahead)
    IF NEW.reading_date > DATE_ADD(CURDATE(), INTERVAL 1 DAY) THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'Reading date cannot be more than 1 day in the future';
    END IF;

    -- Prevent readings older than 30 days without CEO approval
    IF NEW.reading_date < DATE_SUB(CURDATE(), INTERVAL 30 DAY) THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'Reading date cannot be more than 30 days old without CEO approval';
    END IF;
END
$$
DELIMITER ;
DELIMITER $$
CREATE TRIGGER `tr_validate_positive_readings` BEFORE INSERT ON `readings` FOR EACH ROW BEGIN
    IF NEW.dip_reading_liters IS NOT NULL AND NEW.dip_reading_liters < 0 THEN
        INSERT INTO system_health_monitoring (check_type, check_status, check_details, severity)
        VALUES ('VALIDATION_ERROR', 'FAILED',
               CONCAT('Negative dip reading: ', NEW.dip_reading_liters, 'L'), 'HIGH');

        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'Dip reading cannot be negative';
    END IF;

    IF NEW.meter_reading_liters IS NOT NULL AND NEW.meter_reading_liters < 0 THEN
        INSERT INTO system_health_monitoring (check_type, check_status, check_details, severity)
        VALUES ('VALIDATION_ERROR', 'FAILED',
               CONCAT('Negative meter reading: ', NEW.meter_reading_liters, 'L'), 'HIGH');

        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'Meter reading cannot be negative';
    END IF;
END
$$
DELIMITER ;

