DELIMITER $$
CREATE TRIGGER `tr_selling_prices_hash_chain` BEFORE INSERT ON `selling_prices` FOR EACH ROW BEGIN
    DECLARE prev_hash VARCHAR(64);

    SELECT hash_current INTO prev_hash
    FROM selling_prices
    WHERE station_id = NEW.station_id AND product_type = NEW.product_type
    ORDER BY created_at DESC
    LIMIT 1;

    SET NEW.hash_previous = prev_hash;
    SET NEW.hash_current = SHA2(CONCAT(
        NEW.station_id,
        NEW.product_type,
        NEW.price_per_liter,
        NEW.effective_from_date,
        NEW.created_by,
        IFNULL(prev_hash, ''),
        UNIX_TIMESTAMP(NOW(6))
    ), 256);
END
$$
DELIMITER ;
DELIMITER $$
CREATE TRIGGER `tr_validate_financial_values` BEFORE INSERT ON `selling_prices` FOR EACH ROW BEGIN
    IF NEW.price_per_liter <= 0 THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'Selling price must be greater than zero';
    END IF;

    IF NEW.margin_per_liter < 0 THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'Margin per liter cannot be negative';
    END IF;
END
$$
DELIMITER ;
DELIMITER $$
CREATE TRIGGER `tr_validate_price_periods` BEFORE INSERT ON `selling_prices` FOR EACH ROW BEGIN
    IF NEW.effective_until_date IS NOT NULL AND NEW.effective_until_time IS NOT NULL THEN
        IF NEW.effective_until_date < NEW.effective_from_date THEN
            INSERT INTO system_health_monitoring (check_type, check_status, check_details, severity)
            VALUES ('VALIDATION_ERROR', 'FAILED',
                   CONCAT('Invalid price period: ', NEW.effective_from_date, ' to ', NEW.effective_until_date), 'HIGH');

            SIGNAL SQLSTATE '45000'
            SET MESSAGE_TEXT = 'Price effective until date cannot be before from date';
        END IF;

        IF NEW.effective_until_date = NEW.effective_from_date AND NEW.effective_until_time <= NEW.effective_from_time THEN
            SIGNAL SQLSTATE '45000'
            SET MESSAGE_TEXT = 'Price effective until time must be after from time on same date';
        END IF;
    END IF;
END
$$
DELIMITER ;

