--
-- Table structure for table `tanks`
--

CREATE TABLE `tanks` (
  `id` bigint UNSIGNED NOT NULL,
  `station_id` bigint UNSIGNED NOT NULL,
  `tank_number` smallint UNSIGNED NOT NULL,
  `product_id` bigint UNSIGNED NOT NULL,
  `capacity_liters` decimal(12,3) NOT NULL,
  `minimum_stock_level_liters` decimal(12,3) NOT NULL DEFAULT '500.000',
  `maximum_stock_level_liters` decimal(12,3) NOT NULL,
  `critical_low_level_liters` decimal(12,3) NOT NULL DEFAULT '200.000',
  `maximum_variance_percentage` decimal(5,2) NOT NULL DEFAULT '2.00',
  `tank_type` enum('UNDERGROUND','ABOVE_GROUND','PORTABLE') COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'UNDERGROUND',
  `tank_material` enum('STEEL','FIBERGLASS','CONCRETE') COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'STEEL',
  `tank_manufacturer` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `tank_serial_number` varchar(100) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `installation_date` date NOT NULL,
  `last_inspection_date` date NOT NULL,
  `next_inspection_date` date NOT NULL,
  `calibration_date` date NOT NULL,
  `calibration_certificate` varchar(100) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
