--
-- Triggers `audit_logs`
--
DELIMITER $$
CREATE TRIGGER `trg_audit_logs_uuid` BEFORE INSERT ON `audit_logs` FOR EACH ROW BEGIN
    IF NEW.audit_uuid IS NULL OR NEW.audit_uuid = '' THEN
        SET NEW.audit_uuid = UUID();
    END IF;
END
$$
DELIMITER ;

