--
-- Triggers `chart_of_accounts`
--
DELIMITER $$
CREATE TRIGGER `trg_chart_of_accounts_integrity` BEFORE UPDATE ON `chart_of_accounts` FOR EACH ROW BEGIN
    DECLARE dependent_transactions INT;

    -- Prevent deactivating accounts with transactions
    IF OLD.is_active = TRUE AND NEW.is_active = FALSE THEN
        SELECT COUNT(*) INTO dependent_transactions
        FROM general_ledger
        WHERE account_id = NEW.id;

        IF dependent_transactions > 0 THEN
            SIGNAL SQLSTATE '45000'
            SET MESSAGE_TEXT = 'Cannot deactivate chart of accounts with existing transactions (IFRS Compliance)';
        END IF;
    END IF;

    -- Prevent changing account type with transactions
    IF OLD.account_type != NEW.account_type THEN
        SELECT COUNT(*) INTO dependent_transactions
        FROM general_ledger
        WHERE account_id = NEW.id;

        IF dependent_transactions > 0 THEN
            SIGNAL SQLSTATE '45000'
            SET MESSAGE_TEXT = 'Cannot change account type with existing transactions (IFRS Compliance)';
        END IF;
    END IF;
END
$$
DELIMITER ;
DELIMITER $$
CREATE TRIGGER `trg_chart_of_accounts_uuid` BEFORE INSERT ON `chart_of_accounts` FOR EACH ROW BEGIN
    IF NEW.account_uuid IS NULL OR NEW.account_uuid = '' THEN
        SET NEW.account_uuid = UUID();
    END IF;
END
$$
DELIMITER ;

