CREATE TRIGGER `trg_trial_balance_validation` AFTER INSERT ON `general_ledger` FOR EACH ROW BEGIN
    DECLARE total_debits DECIMAL(15,2);
    DECLARE total_credits DECIMAL(15,2);
    DECLARE balance_difference DECIMAL(15,2);

    -- Calculate station trial balance
    SELECT
        IFNULL(SUM(debit_amount_ugx), 0),
        IFNULL(SUM(credit_amount_ugx), 0)
    INTO total_debits, total_credits
    FROM general_ledger
    WHERE station_id = NEW.station_id
    AND posting_date <= CURDATE();

    SET balance_difference = total_debits - total_credits;

    -- Log warning if out of balance beyond tolerance
    IF ABS(balance_difference) > 1000 THEN
        INSERT INTO audit_logs (
            station_id, audit_action, table_name, record_id,
            risk_level, business_impact, compliance_relevance
        ) VALUES (
            NEW.station_id, 'TRIAL_BALANCE_WARNING', 'general_ledger', NEW.id,
            'HIGH',
            CONCAT('Trial balance out of balance by UGX ', balance_difference),
            'IFRS Compliance - Double Entry Accounting Violation'
        );
    END IF;
END
$$
DELIMITER ;

