CREATE TRIGGER `trg_journal_entry_balance_validation` BEFORE UPDATE ON `journal_entries` FOR EACH ROW BEGIN
    DECLARE total_debits DECIMAL(15,2) DEFAULT 0;
    DECLARE total_credits DECIMAL(15,2) DEFAULT 0;

    -- Calculate totals from general ledger
    SELECT
        IFNULL(SUM(debit_amount_ugx), 0),
        IFNULL(SUM(credit_amount_ugx), 0)
    INTO total_debits, total_credits
    FROM general_ledger
    WHERE journal_entry_id = NEW.id;

    -- Ensure balance (IFRS requirement)
    IF total_debits != total_credits THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'Journal entry must balance: Debits must equal Credits (IFRS Compliance)';
    END IF;

    -- Update totals
    SET NEW.total_debit_ugx = total_debits;
    SET NEW.total_credit_ugx = total_credits;

    -- Auto-post if approved
    IF NEW.journal_status = 'PENDING_APPROVAL' AND NEW.approved_by IS NOT NULL THEN
        SET NEW.journal_status = 'POSTED';
        SET NEW.posted_date = NOW();
        SET NEW.posted_by = NEW.approved_by;
    END IF;
END
$$
