    FROM financial_periods
    WHERE id = NEW.period_id;

    IF period_status_check IN ('CLOSED', 'LOCKED') THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'Cannot post transactions to closed or locked financial periods (IFRS Compliance)';
    END IF;
END
$$
