    DECLARE current_period_id BIGINT UNSIGNED;
    DECLARE journal_id BIGINT UNSIGNED;
    DECLARE line_counter INT DEFAULT 1;

    -- Only process when payroll status changes to 'APPROVED'
    IF OLD.payroll_status != 'APPROVED' AND NEW.payroll_status = 'APPROVED' THEN

        -- Get required accounts
        SELECT id INTO salary_account_id
        FROM chart_of_accounts
        WHERE station_id = NEW.station_id AND account_purpose = 'SALARIES_WAGES'
        AND is_active = 1 LIMIT 1;

        SELECT id INTO bank_account_id
        FROM chart_of_accounts
        WHERE station_id = NEW.station_id AND is_bank_account = 1
        AND is_active = 1 LIMIT 1;

        SELECT id INTO paye_account_id
        FROM chart_of_accounts
        WHERE station_id = NEW.station_id AND account_purpose = 'TAX_PAYABLE_PAYE'
        AND is_active = 1 LIMIT 1;

        SELECT id INTO nssf_employee_account_id
        FROM chart_of_accounts
        WHERE station_id = NEW.station_id AND account_purpose = 'NSSF_EMPLOYEE_PAYABLE'
        AND is_active = 1 LIMIT 1;

        SELECT id INTO nssf_employer_account_id
        FROM chart_of_accounts
        WHERE station_id = NEW.station_id AND account_purpose = 'NSSF_EMPLOYER_EXPENSE'
        AND is_active = 1 LIMIT 1;

        -- Get current open period
        SELECT id INTO current_period_id
        FROM financial_periods
        WHERE station_id = NEW.station_id AND period_status = 'OPEN'
        AND CURDATE() BETWEEN start_date AND end_date LIMIT 1;

        -- Create journal entry with exact field names
        IF salary_account_id IS NOT NULL AND bank_account_id IS NOT NULL AND current_period_id IS NOT NULL THEN

            INSERT INTO journal_entries (
                journal_uuid, station_id, period_id, journal_type, reference_type, reference_id,
                transaction_date, description, total_debit_ugx, total_credit_ugx,
                prepared_by, journal_status, auto_generated, posted_date, posted_by, created_at
            ) VALUES (
                UUID(), NEW.station_id, current_period_id, 'PAYROLL', 'PAYROLL_RUN', NEW.id,
                CURDATE(),
                CONCAT('Payroll Journal - ', NEW.payroll_month, '/', NEW.payroll_year, ' - ', NEW.total_employees, ' employees'),
                NEW.total_gross_pay_ugx + COALESCE(NEW.total_employer_nssf_ugx, 0),
                NEW.total_gross_pay_ugx + COALESCE(NEW.total_employer_nssf_ugx, 0),
                NEW.approved_by, 'POSTED', 1, NOW(), NEW.approved_by, NOW()
            );

            SET journal_id = LAST_INSERT_ID();

            -- 1. Debit: Salary Expense
            INSERT INTO general_ledger (
                ledger_uuid, journal_entry_id, station_id, account_id, line_number,
                transaction_date, posting_date, debit_amount_ugx, description, created_at
            ) VALUES (
