                CONCAT('Salary expense - ', NEW.payroll_month, '/', NEW.payroll_year), NOW()
            );
            SET line_counter = line_counter + 1;

            -- 2. Debit: NSSF Employer Expense (using correct field name)
            IF nssf_employer_account_id IS NOT NULL AND COALESCE(NEW.total_employer_nssf_ugx, 0) > 0 THEN
                INSERT INTO general_ledger (
                    ledger_uuid, journal_entry_id, station_id, account_id, line_number,
                    transaction_date, posting_date, debit_amount_ugx, description, created_at
                ) VALUES (
                    UUID(), journal_id, NEW.station_id, nssf_employer_account_id, line_counter,
                    CURDATE(), CURDATE(), NEW.total_employer_nssf_ugx,
                    CONCAT('NSSF employer contribution - ', NEW.payroll_month, '/', NEW.payroll_year), NOW()
                );
                SET line_counter = line_counter + 1;
            END IF;

            -- 3. Credit: PAYE Tax Payable (will be zero in your system)
            IF paye_account_id IS NOT NULL AND COALESCE(NEW.total_paye_ugx, 0) > 0 THEN
                INSERT INTO general_ledger (
                    ledger_uuid, journal_entry_id, station_id, account_id, line_number,
                    transaction_date, posting_date, credit_amount_ugx, description, created_at
