CREATE TRIGGER `trg_meter_reading_calculation` BEFORE INSERT ON `pump_meter_readings` FOR EACH ROW BEGIN
    DECLARE last_reading DECIMAL(12,3) DEFAULT 0.000;
    DECLARE nozzle_digits INT DEFAULT 8;
    DECLARE max_reading DECIMAL(12,3);

    -- Get previous reading
    SELECT meter_reading INTO last_reading
    FROM pump_meter_readings
    WHERE nozzle_id = NEW.nozzle_id
    AND measurement_date <= NEW.measurement_date
    ORDER BY measurement_date DESC, measurement_time DESC
    LIMIT 1;

    -- Get nozzle specifications
    SELECT meter_digits INTO nozzle_digits
    FROM pump_nozzles
    WHERE id = NEW.nozzle_id;

    -- Calculate maximum reading based on meter digits
    SET max_reading = POW(10, nozzle_digits) - 1;

    -- Set previous reading
    SET NEW.previous_reading = IFNULL(last_reading, 0.000);

    -- Handle meter rollover
    IF NEW.meter_reading < NEW.previous_reading THEN
        SET NEW.meter_rollover = 1;
        SET NEW.rollover_value = max_reading;
        SET NEW.total_dispensed = (max_reading - NEW.previous_reading) + NEW.meter_reading;
    ELSE
        SET NEW.total_dispensed = NEW.meter_reading - NEW.previous_reading;
    END IF;

    -- Update current meter reading in nozzles table
    UPDATE pump_nozzles
    SET current_meter_reading = NEW.meter_reading,
        updated_at = NOW()
    WHERE id = NEW.nozzle_id;
END
$$
