CREATE TRIGGER `trg_comprehensive_shift_closure` AFTER UPDATE ON `shifts` FOR EACH ROW BEGIN
    DECLARE total_variance_value DECIMAL(15,2) DEFAULT 0.00;
    DECLARE fuel_variance_count INT DEFAULT 0;
    DECLARE nonfuel_variance_count INT DEFAULT 0;
    DECLARE shop_variance_count INT DEFAULT 0;
    DECLARE critical_variance_count INT DEFAULT 0;

    -- Only process when shift status changes to 'COMPLETED'
    IF OLD.shift_status != 'COMPLETED' AND NEW.shift_status = 'COMPLETED' THEN

        -- Aggregate shift performance data
        SELECT
            COALESCE(SUM(ABS(sr.variance_value_ugx)), 0.00),
            COALESCE(SUM(CASE WHEN p.product_category = 'FUEL' AND ABS(sr.variance_percentage) > 1.00 THEN 1 ELSE 0 END), 0),
            COALESCE(SUM(CASE WHEN p.product_category = 'NON_FUEL' AND ABS(sr.variance_percentage) > 2.00 THEN 1 ELSE 0 END), 0),
            COALESCE(SUM(CASE WHEN p.product_category = 'SHOP' AND ABS(sr.variance_percentage) > 3.00 THEN 1 ELSE 0 END), 0),
            COALESCE(SUM(CASE WHEN ABS(sr.variance_value_ugx) > 500000 THEN 1 ELSE 0 END), 0)
        INTO
            total_variance_value,
            fuel_variance_count,
            nonfuel_variance_count,
            shop_variance_count,
            critical_variance_count
        FROM stock_reconciliations sr
        JOIN products p ON sr.product_id = p.id
        WHERE sr.shift_id = NEW.id
          AND sr.station_id = NEW.station_id
          AND sr.reconciliation_type = 'CLOSING';

        -- Create comprehensive audit log entry (NO APPROVAL WORKFLOWS)
        INSERT INTO audit_logs (
            audit_uuid,
            station_id,
            user_id,
            session_id,
            audit_timestamp,
            audit_action,
            table_name,
            record_id,
            field_changes,
            business_impact,
            compliance_relevance,
            audit_metadata,
            severity_level,
            created_at
        ) VALUES (
            UUID(),
            NEW.station_id,
            NEW.shift_supervisor_id,
            'SYSTEM_AUTOMATED',
            NOW(),
            'SHIFT_CLOSURE_AUTOMATION',
            'shifts',
            NEW.id,
            JSON_OBJECT(
                'shift_performance', JSON_OBJECT(
                    'total_variance_ugx', total_variance_value,
                    'fuel_variances', fuel_variance_count,
                    'nonfuel_variances', nonfuel_variance_count,
                    'shop_variances', shop_variance_count,
                    'critical_variances', critical_variance_count
                ),
                'automation_summary', JSON_OBJECT(
                    'auto_calculations_completed', TRUE,
                    'performance_scorecard_generated', TRUE,
                    'variance_summary_created', TRUE
                )
            ),
            CONCAT('Automated shift closure processing completed - Total variance impact: UGX ', FORMAT(total_variance_value, 0)),
            'ISO 27001 - Automated Operations Control, Financial Controls',
            JSON_OBJECT(
                'system_context', JSON_OBJECT(
                    'trigger_name', 'trg_comprehensive_shift_closure',
                    'automation_level', 'FULL',
                    'database_engine', 'MySQL'
                ),
                'performance_context', JSON_OBJECT(
                    'shift_duration_hours', TIMESTAMPDIFF(HOUR, NEW.planned_start_time, NEW.planned_end_time),
                    'total_reconciliations', (fuel_variance_count + nonfuel_variance_count + shop_variance_count),
                    'variance_threshold_breaches', critical_variance_count
                )
            ),
            IF(total_variance_value > 500000, 'HIGH', IF(total_variance_value > 100000, 'MEDIUM', 'LOW')),
            NOW()
        );

    END IF;

END
$$
