    DECLARE tank_station_id BIGINT UNSIGNED;

    -- Ensure pump and tank belong to same station
    SELECT station_id INTO tank_station_id
    FROM station_tanks
    WHERE id = NEW.connected_tank_id;

    IF tank_station_id != NEW.station_id THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'Pump and connected tank must belong to same station';
    END IF;

