    IF NEW.number_of_nozzles < 1 OR NEW.number_of_nozzles > 12 THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'Number of nozzles must be between 1 and 12';
    END IF;
END
