DELIMITER $$
CREATE TRIGGER `trg_auto_calculate_fuel_sales` BEFORE INSERT ON `stock_reconciliations` FOR EACH ROW BEGIN
    DECLARE total_pump_dispensed DECIMAL(12,3) DEFAULT 0.000;
    DECLARE product_type VARCHAR(20);

    IF NEW.reconciliation_type = 'CLOSING' THEN
        SELECT product_category INTO product_type
        FROM products
        WHERE id = NEW.product_id;


        IF product_type = 'FUEL' THEN
            -- ✅ FIXED: Use correct table name 'pump_nozzles' instead of 'nozzles'
            SELECT COALESCE(SUM(
                CASE
                    WHEN pmr.meter_rollover = 1 THEN
                        ((pmr.rollover_value - pmr.previous_reading) + pmr.meter_reading)
                    ELSE
                        (pmr.meter_reading - pmr.previous_reading)
                END
            ), 0.000)
            INTO total_pump_dispensed
            FROM pump_meter_readings pmr
            JOIN pump_nozzles pn ON pmr.nozzle_id = pn.id  -- ✅ FIXED: pump_nozzles
            JOIN station_pumps sp ON pn.pump_id = sp.id     -- ✅ FIXED: pn.pump_id
            JOIN station_tanks st ON sp.connected_tank_id = st.id
            WHERE pmr.station_id = NEW.station_id
              AND pmr.shift_id = NEW.shift_id
              AND pmr.reading_type = 'CLOSING'
              AND st.product_id = NEW.product_id
              AND pmr.measurement_date = NEW.reconciliation_date;

            IF NEW.sales_quantity = 0.000 OR NEW.sales_quantity IS NULL THEN
                SET NEW.sales_quantity = total_pump_dispensed;
            END IF;
        END IF;
    END IF;
END
$$
