DELIMITER $$
CREATE TRIGGER `trg_auto_populate_opening_stock` BEFORE INSERT ON `stock_reconciliations` FOR EACH ROW BEGIN
    DECLARE previous_closing_stock DECIMAL(12,3) DEFAULT 0.000;
    DECLARE previous_shift_status VARCHAR(20);

    -- Only process OPENING reconciliations
    IF NEW.reconciliation_type = 'OPENING' THEN

        -- Get the most recent CLOSING reconciliation for same product/station
        -- Using optimized index: idx_stock_trigger_lookup
        SELECT
            sr.actual_closing,
            s.shift_status
        INTO
            previous_closing_stock,
            previous_shift_status
        FROM stock_reconciliations sr
        JOIN shifts s ON sr.shift_id = s.id
        WHERE sr.product_id = NEW.product_id
          AND sr.station_id = NEW.station_id
          AND sr.reconciliation_type = 'CLOSING'
          AND sr.reconciliation_date < NEW.reconciliation_date
          AND s.shift_status = 'CLOSED'
        ORDER BY sr.reconciliation_date DESC, sr.id DESC
        LIMIT 1;

        -- Auto-populate opening_stock if not manually provided and previous shift was properly closed
        IF (NEW.opening_stock = 0.000 OR NEW.opening_stock IS NULL)
           AND previous_shift_status = 'CLOSED'
           AND previous_closing_stock IS NOT NULL THEN

            SET NEW.opening_stock = previous_closing_stock;

        END IF;

    END IF;

END
$$
