DELIMITER $$
CREATE TRIGGER `trg_auto_populate_sales_quantity` BEFORE INSERT ON `stock_reconciliations` FOR EACH ROW BEGIN
    DECLARE total_pump_dispensed DECIMAL(12,3) DEFAULT 0.000;

    -- Only auto-populate for CLOSING reconciliations of FUEL products
    IF NEW.reconciliation_type = 'CLOSING' THEN
        -- Get product category to ensure it's fuel
        IF (SELECT product_category FROM products WHERE id = NEW.product_id) = 'FUEL' THEN

            -- FIXED: Calculate total fuel dispensed using CORRECT table names
            SELECT IFNULL(SUM(
                CASE
                    WHEN pmr.meter_rollover = 1 THEN
                        ((pmr.rollover_value - pmr.previous_reading) + pmr.meter_reading)
                    ELSE
                        (pmr.meter_reading - pmr.previous_reading)
                END
            ), 0.000)
            INTO total_pump_dispensed
            FROM pump_meter_readings pmr
            JOIN pump_nozzles pn ON pmr.nozzle_id = pn.id    -- ✅ FIXED: pump_nozzles (not nozzles)
            JOIN station_pumps sp ON pn.pump_id = sp.id       -- ✅ FIXED: pn.pump_id (not n.pump_id)
            JOIN station_tanks st ON sp.connected_tank_id = st.id
            WHERE pmr.station_id = NEW.station_id
              AND pmr.shift_id = NEW.shift_id
              AND pmr.reading_type = 'CLOSING'
              AND st.product_id = NEW.product_id
              AND pmr.measurement_date = NEW.reconciliation_date;

            -- Auto-populate sales_quantity if not manually provided
            IF NEW.sales_quantity = 0.000 OR NEW.sales_quantity IS NULL THEN
                SET NEW.sales_quantity = total_pump_dispensed;
            END IF;

        END IF;
    END IF;

END
$$
