DELIMITER $$
CREATE TRIGGER `trg_enforce_approval_workflows` BEFORE UPDATE ON `stock_reconciliations` FOR EACH ROW BEGIN
    DECLARE adjustment_amount DECIMAL(15,2);
    DECLARE pending_approval_count INT;

    -- Calculate adjustment value
    SET adjustment_amount = ABS(NEW.variance_value_ugx);

    -- Check if large adjustment requires approval
    IF adjustment_amount > 500000 AND OLD.verified_by IS NULL AND NEW.verified_by IS NOT NULL THEN

        -- Check if approval workflow exists
        SELECT COUNT(*) INTO pending_approval_count
        FROM approval_workflows
        WHERE reference_type = 'STOCK_RECONCILIATION'
        AND reference_id = NEW.id
        AND approval_status = 'PENDING';

        -- Require approval for large adjustments
        IF pending_approval_count = 0 THEN
            SIGNAL SQLSTATE '45000'
            SET MESSAGE_TEXT = 'Stock adjustments over UGX 500,000 require approval workflow';
        END IF;
    END IF;
END
$$
