DELIMITER $$
CREATE TRIGGER `trg_enhanced_auto_journal_stock_movements` AFTER INSERT ON `stock_reconciliations` FOR EACH ROW BEGIN
    DECLARE inventory_account_id BIGINT UNSIGNED;
    DECLARE variance_account_id BIGINT UNSIGNED;
    DECLARE revenue_account_id BIGINT UNSIGNED;
    DECLARE cogs_account_id BIGINT UNSIGNED;
    DECLARE cash_account_id BIGINT UNSIGNED;
    DECLARE current_period_id BIGINT UNSIGNED;
    DECLARE journal_id BIGINT UNSIGNED;
    DECLARE variance_value DECIMAL(15,2);
    DECLARE sales_revenue_value DECIMAL(15,2);
    DECLARE cogs_value DECIMAL(15,2);
    DECLARE prod_category VARCHAR(20);
    DECLARE product_cost DECIMAL(15,2);
    DECLARE product_price DECIMAL(15,2);
    DECLARE product_name VARCHAR(100);
    DECLARE inventory_purpose VARCHAR(50);
    DECLARE revenue_purpose VARCHAR(50);
    DECLARE cogs_description VARCHAR(100);

    -- Get product information with NULL protection
    SELECT
        IFNULL(product_category, 'UNKNOWN'),
        IFNULL(current_cost_price, 0),
        IFNULL(current_selling_price, 0),
        IFNULL(product_name, 'Unknown Product')
    INTO
        prod_category,
        product_cost,
        product_price,
        product_name
    FROM products WHERE id = NEW.product_id;

    -- Get current financial period
    SELECT id INTO current_period_id
    FROM financial_periods
    WHERE station_id = NEW.station_id
    AND period_status = 'OPEN'
    AND NEW.reconciliation_date BETWEEN start_date AND end_date
    LIMIT 1;

    -- Only proceed if we have a valid period
    IF current_period_id IS NOT NULL THEN

        -- Determine account purposes dynamically for ALL categories
        SET inventory_purpose = CASE
            WHEN prod_category = 'FUEL' THEN 'INVENTORY_FUEL_PETROL'
            WHEN prod_category = 'NON_FUEL' THEN 'INVENTORY_NON_FUEL'
            WHEN prod_category = 'SHOP' THEN 'INVENTORY_SHOP'
            ELSE 'INVENTORY_GENERAL'
        END;

        SET revenue_purpose = CASE
            WHEN prod_category = 'FUEL' THEN 'FUEL_SALES_REVENUE'
            WHEN prod_category = 'NON_FUEL' THEN 'NON_FUEL_SALES'
            WHEN prod_category = 'SHOP' THEN 'SHOP_SALES'
            ELSE 'OTHER_SALES'
        END;

        SET cogs_description = CASE
            WHEN prod_category = 'FUEL' THEN 'Fuel'
            WHEN prod_category = 'NON_FUEL' THEN 'Non-Fuel'
            WHEN prod_category = 'SHOP' THEN 'Shop'
            ELSE 'General'
        END;

        -- Get relevant account IDs
        SELECT id INTO inventory_account_id
        FROM chart_of_accounts
        WHERE station_id = NEW.station_id
        AND account_purpose = inventory_purpose
        AND is_active = TRUE
        LIMIT 1;

        SELECT id INTO variance_account_id
        FROM chart_of_accounts
        WHERE station_id = NEW.station_id
        AND account_purpose = 'INVENTORY_VARIANCE'
        AND is_active = TRUE
        LIMIT 1;

        SELECT id INTO revenue_account_id
        FROM chart_of_accounts
        WHERE station_id = NEW.station_id
        AND account_purpose = revenue_purpose
        AND is_active = TRUE
        LIMIT 1;

        SELECT id INTO cogs_account_id
        FROM chart_of_accounts
        WHERE station_id = NEW.station_id
        AND account_purpose = 'COST_OF_GOODS_SOLD'
        AND is_active = TRUE
        LIMIT 1;

        SELECT id INTO cash_account_id
        FROM chart_of_accounts
        WHERE station_id = NEW.station_id
        AND account_purpose = 'CASH_ON_HAND'
        AND is_active = TRUE
        LIMIT 1;

        -- Process variance entries if significant
        IF ABS(NEW.variance_value_ugx) > 1000 AND variance_account_id IS NOT NULL AND inventory_account_id IS NOT NULL THEN

            SET variance_value = NEW.variance_value_ugx;

            INSERT INTO journal_entries (
                station_id, period_id, journal_number, journal_type, reference_type, reference_id,
                transaction_date, description, total_debit_ugx, total_credit_ugx,
                prepared_by, journal_status, auto_generated
            ) VALUES (
                NEW.station_id, current_period_id,
                CONCAT('STK-', DATE_FORMAT(NEW.reconciliation_date, '%Y%m%d'), '-', LPAD(NEW.id, 6, '0')),
                'STOCK', 'STOCK_RECONCILIATION', NEW.id,
                NEW.reconciliation_date,
                CONCAT('Stock variance adjustment for ', IFNULL(product_name, 'Unknown Product'), ' - Variance: UGX ', ROUND(variance_value, 2)),
                ABS(variance_value), ABS(variance_value), NEW.recorded_by, 'POSTED', TRUE
            );

            SET journal_id = LAST_INSERT_ID();

            -- Create ledger entries based on variance direction
            IF variance_value > 0 THEN
                INSERT INTO general_ledger (
                    journal_entry_id, station_id, account_id, line_number,
                    transaction_date, posting_date, debit_amount_ugx, description
                ) VALUES (
                    journal_id, NEW.station_id, inventory_account_id, 1,
                    NEW.reconciliation_date, CURDATE(), variance_value,
                    CONCAT('Inventory increase - ', IFNULL(product_name, 'Unknown Product'), ' - Positive variance')
                );

                INSERT INTO general_ledger (
                    journal_entry_id, station_id, account_id, line_number,
                    transaction_date, posting_date, credit_amount_ugx, description
                ) VALUES (
                    journal_id, NEW.station_id, variance_account_id, 2,
                    NEW.reconciliation_date, CURDATE(), variance_value,
                    CONCAT('Inventory variance credit - ', IFNULL(product_name, 'Unknown Product'))
                );
            ELSE
                INSERT INTO general_ledger (
                    journal_entry_id, station_id, account_id, line_number,
                    transaction_date, posting_date, debit_amount_ugx, description
                ) VALUES (
                    journal_id, NEW.station_id, variance_account_id, 1,
                    NEW.reconciliation_date, CURDATE(), ABS(variance_value),
                    CONCAT('Inventory variance debit - ', IFNULL(product_name, 'Unknown Product'))
                );

                INSERT INTO general_ledger (
                    journal_entry_id, station_id, account_id, line_number,
                    transaction_date, posting_date, credit_amount_ugx, description
                ) VALUES (
                    journal_id, NEW.station_id, inventory_account_id, 2,
                    NEW.reconciliation_date, CURDATE(), ABS(variance_value),
                    CONCAT('Inventory decrease - ', IFNULL(product_name, 'Unknown Product'), ' - Negative variance')
                );
            END IF;
        END IF;

        -- Process sales revenue and COGS for CLOSING reconciliations with sales
        IF NEW.reconciliation_type = 'CLOSING' AND NEW.sales_quantity > 0 THEN

            SET sales_revenue_value = NEW.sales_quantity * product_price;
            SET cogs_value = NEW.sales_quantity * product_cost;

            -- Create Sales Revenue journal entry if revenue account exists
            IF revenue_account_id IS NOT NULL AND cash_account_id IS NOT NULL THEN

                INSERT INTO journal_entries (
                    station_id, period_id, journal_number, journal_type, reference_type, reference_id,
                    transaction_date, description, total_debit_ugx, total_credit_ugx,
                    prepared_by, journal_status, auto_generated
                ) VALUES (
                    NEW.station_id, current_period_id,
                    CONCAT('SAL-', DATE_FORMAT(NEW.reconciliation_date, '%Y%m%d'), '-', LPAD(NEW.id, 6, '0')),
                    'SALES', 'STOCK_RECONCILIATION', NEW.id,
                    NEW.reconciliation_date,
                    CONCAT(IFNULL(cogs_description, 'General'), ' Sales Revenue - ', IFNULL(product_name, 'Unknown Product'), ' - ', NEW.sales_quantity, ' units'),
                    sales_revenue_value, sales_revenue_value, NEW.recorded_by, 'POSTED', TRUE
                );

                SET journal_id = LAST_INSERT_ID();

                INSERT INTO general_ledger (
                    journal_entry_id, station_id, account_id, line_number,
                    transaction_date, posting_date, debit_amount_ugx, description
                ) VALUES (
                    journal_id, NEW.station_id, cash_account_id, 1,
                    NEW.reconciliation_date, CURDATE(), sales_revenue_value,
                    CONCAT(IFNULL(cogs_description, 'General'), ' Sales - Cash received - ', IFNULL(product_name, 'Unknown Product'))
                );

                INSERT INTO general_ledger (
                    journal_entry_id, station_id, account_id, line_number,
                    transaction_date, posting_date, credit_amount_ugx, description
                ) VALUES (
                    journal_id, NEW.station_id, revenue_account_id, 2,
                    NEW.reconciliation_date, CURDATE(), sales_revenue_value,
                    CONCAT(IFNULL(cogs_description, 'General'), ' Sales Revenue - ', IFNULL(product_name, 'Unknown Product'))
                );
            END IF;

            -- Create Cost of Goods Sold journal entry if COGS account exists
            IF cogs_account_id IS NOT NULL AND inventory_account_id IS NOT NULL THEN

                INSERT INTO journal_entries (
                    station_id, period_id, journal_number, journal_type, reference_type, reference_id,
                    transaction_date, description, total_debit_ugx, total_credit_ugx,
                    prepared_by, journal_status, auto_generated
                ) VALUES (
                    NEW.station_id, current_period_id,
                    CONCAT('COG-', DATE_FORMAT(NEW.reconciliation_date, '%Y%m%d'), '-', LPAD(NEW.id, 6, '0')),
                    'SALES', 'STOCK_RECONCILIATION', NEW.id,
                    NEW.reconciliation_date,
                    CONCAT(IFNULL(cogs_description, 'General'), ' COGS - ', IFNULL(product_name, 'Unknown Product'), ' - ', NEW.sales_quantity, ' units'),
                    cogs_value, cogs_value, NEW.recorded_by, 'POSTED', TRUE
                );

                SET journal_id = LAST_INSERT_ID();

                INSERT INTO general_ledger (
                    journal_entry_id, station_id, account_id, line_number,
                    transaction_date, posting_date, debit_amount_ugx, description
                ) VALUES (
                    journal_id, NEW.station_id, cogs_account_id, 1,
                    NEW.reconciliation_date, CURDATE(), cogs_value,
                    CONCAT(IFNULL(cogs_description, 'General'), ' Cost of Goods Sold - ', IFNULL(product_name, 'Unknown Product'))
                );

                INSERT INTO general_ledger (
                    journal_entry_id, station_id, account_id, line_number,
                    transaction_date, posting_date, credit_amount_ugx, description
                ) VALUES (
                    journal_id, NEW.station_id, inventory_account_id, 2,
                    NEW.reconciliation_date, CURDATE(), cogs_value,
                    CONCAT(IFNULL(cogs_description, 'General'), ' Inventory reduction - ', IFNULL(product_name, 'Unknown Product'))
                );
            END IF;
        END IF;

    END IF;
END
$$
