CREATE TRIGGER `trg_fraud_detection_stock_variances` AFTER INSERT ON `stock_reconciliations` FOR EACH ROW BEGIN
    DECLARE fraud_threshold DECIMAL(5,2);
    DECLARE consecutive_variances INT;
    DECLARE ceo_user_id BIGINT UNSIGNED;
    DECLARE prod_category VARCHAR(20);

    -- Get product category
    SELECT product_category INTO prod_category
    FROM products WHERE id = NEW.product_id;

    -- Set fraud thresholds
    SET fraud_threshold = CASE
        WHEN prod_category = 'FUEL' THEN 2.0
        WHEN prod_category = 'NON_FUEL' THEN 5.0
        ELSE 8.0
    END;

    -- Check for fraud indicators
    IF ABS(NEW.variance_percentage) > fraud_threshold THEN

        -- Count consecutive high variances
        SELECT COUNT(*) INTO consecutive_variances
        FROM stock_reconciliations sr
        JOIN products p ON sr.product_id = p.id
        WHERE sr.station_id = NEW.station_id
        AND sr.recorded_by = NEW.recorded_by
        AND ABS(sr.variance_percentage) > fraud_threshold
        AND sr.reconciliation_date >= DATE_SUB(NEW.reconciliation_date, INTERVAL 7 DAY)
        AND p.product_category = prod_category;

        -- Trigger investigation if 3+ consecutive variances
        IF consecutive_variances >= 3 THEN

            -- Find CEO for escalation
            SELECT id INTO ceo_user_id
            FROM users
            WHERE user_role = 'CEO' AND default_station_id = NEW.station_id
            LIMIT 1;

            -- Create urgent approval workflow
            INSERT INTO approval_workflows (
                approval_uuid, station_id, approval_type, reference_type, reference_id,
                approval_tier, requested_by, request_reason, current_approver,
                is_emergency, escalation_required
            ) VALUES (
                UUID(), NEW.station_id, 'EMERGENCY_OVERRIDE', 'STOCK_RECONCILIATION', NEW.id,
                'CEO', NEW.recorded_by,
                CONCAT('FRAUD ALERT: ', consecutive_variances, ' consecutive high variances by same user'),
                ceo_user_id, TRUE, TRUE
            );

            -- Log critical audit entry
            INSERT INTO audit_logs (
                station_id, user_id, audit_action, table_name, record_id,
                risk_level, fraud_indicators, business_impact
            ) VALUES (
                NEW.station_id, NEW.recorded_by, 'FRAUD_DETECTION', 'stock_reconciliations', NEW.id,
                'CRITICAL',
                JSON_ARRAY(CONCAT('High variance: ', NEW.variance_percentage, '%'),
                          CONCAT('Consecutive occurrences: ', consecutive_variances)),
                'Potential inventory fraud detected'
            );
        END IF;
    END IF;
END
$$
