CREATE TRIGGER `trg_stock_calculations` BEFORE INSERT ON `stock_reconciliations` FOR EACH ROW BEGIN
    DECLARE threshold_pct DECIMAL(5,2);
    DECLARE prod_category VARCHAR(20);
    DECLARE product_cost DECIMAL(15,2);
    DECLARE variance_value DECIMAL(15,2);

    -- Calculate expected closing
    SET NEW.expected_closing = NEW.opening_stock + NEW.deliveries_received - NEW.sales_quantity + NEW.adjustments;

    -- Calculate variance quantity
    SET NEW.variance_quantity = NEW.actual_closing - NEW.expected_closing;

    -- Calculate variance percentage
    IF NEW.expected_closing > 0 THEN
        SET NEW.variance_percentage = (NEW.variance_quantity / NEW.expected_closing) * 100;
    ELSE
        SET NEW.variance_percentage = 0;
    END IF;

    -- Get product info
    SELECT current_cost_price, product_category
    INTO product_cost, prod_category
    FROM products WHERE id = NEW.product_id;

    -- Calculate variance value
    SET variance_value = NEW.variance_quantity * product_cost;
    SET NEW.variance_value_ugx = variance_value;

    -- Set investigation threshold by category
    SET threshold_pct = CASE
        WHEN prod_category = 'FUEL' THEN 1.0
        WHEN prod_category = 'NON_FUEL' THEN 2.0
        ELSE 3.0
    END;

    -- Flag for investigation if variance exceeds threshold
    IF ABS(NEW.variance_percentage) > threshold_pct THEN
        SET NEW.investigation_required = TRUE;
    END IF;

    -- Set reconciliation time if not provided
    IF NEW.reconciliation_time IS NULL THEN
        SET NEW.reconciliation_time = CURRENT_TIME;
    END IF;
END
$$
