CREATE TRIGGER `trg_update_inventory_costs` AFTER INSERT ON `stock_reconciliations` FOR EACH ROW BEGIN
    DECLARE new_weighted_average_cost DECIMAL(15,2);
    DECLARE current_stock_quantity DECIMAL(12,3);
    DECLARE current_product_cost DECIMAL(15,2);
    DECLARE delivery_unit_cost DECIMAL(15,2);
    DECLARE total_existing_value DECIMAL(18,2);
    DECLARE total_new_value DECIMAL(18,2);
    DECLARE total_combined_value DECIMAL(18,2);
    DECLARE total_combined_quantity DECIMAL(12,3);
    DECLARE audit_user_id BIGINT UNSIGNED;
    DECLARE audit_session_id VARCHAR(100);

    -- Only process DELIVERY reconciliations with actual deliveries
    IF NEW.reconciliation_type = 'DELIVERY' AND NEW.deliveries_received > 0 THEN

        -- Get current product cost price with station isolation
        SELECT current_cost_price INTO current_product_cost
        FROM products
        WHERE id = NEW.product_id
        AND station_id = NEW.station_id;

        -- Get previous stock level (exclude current record)
        SELECT IFNULL(actual_closing, 0) INTO current_stock_quantity
        FROM stock_reconciliations
        WHERE product_id = NEW.product_id
        AND station_id = NEW.station_id
        AND reconciliation_date <= NEW.reconciliation_date
        AND id < NEW.id
        ORDER BY reconciliation_date DESC, id DESC
        LIMIT 1;

        -- Extract delivery cost from reconciliation_data JSON
        SET delivery_unit_cost = CAST(
            JSON_UNQUOTE(JSON_EXTRACT(NEW.reconciliation_data, '$.delivery_cost_per_unit'))
            AS DECIMAL(15,2)
        );

        -- Fallback validation
        IF delivery_unit_cost IS NULL OR delivery_unit_cost <= 0 THEN
            SET delivery_unit_cost = current_product_cost;
        END IF;

        -- Calculate proper weighted average cost
        IF current_stock_quantity > 0 THEN
            SET total_existing_value = current_stock_quantity * current_product_cost;
            SET total_new_value = NEW.deliveries_received * delivery_unit_cost;
            SET total_combined_value = total_existing_value + total_new_value;
            SET total_combined_quantity = current_stock_quantity + NEW.deliveries_received;

            IF total_combined_quantity > 0 THEN
                SET new_weighted_average_cost = total_combined_value / total_combined_quantity;
            ELSE
                SET new_weighted_average_cost = delivery_unit_cost;
            END IF;
        ELSE
            SET new_weighted_average_cost = delivery_unit_cost;
        END IF;

        -- Update product cost with proper station isolation
        UPDATE products
        SET current_cost_price = new_weighted_average_cost,
            updated_at = NOW()
        WHERE id = NEW.product_id
        AND station_id = NEW.station_id;

        -- ✅ SAFE AUDIT LOGGING - MATCHES EXISTING CONTROLLER PATTERNS
        -- Validate user exists to prevent foreign key violations
        SELECT COUNT(*) INTO audit_user_id
        FROM users
        WHERE id = NEW.recorded_by;

        -- Only log if user exists and significant cost change occurred
        IF audit_user_id > 0 AND ABS(new_weighted_average_cost - current_product_cost) > 1.00 THEN

            -- ✅ SAFE: Use minimal audit logging to prevent conflicts
            INSERT INTO audit_logs (
                audit_uuid,
                station_id,
                user_id,
                session_id,
                audit_timestamp,
                audit_action,
                table_name,
                record_id,
                field_changes,
                ip_address,
                user_agent,
                geographic_location,
                risk_level,
                fraud_indicators,
                business_impact,
                compliance_relevance,
                audit_metadata,
                retention_period_years,
                created_at
            ) VALUES (
                UUID(),
                NEW.station_id,
                NEW.recorded_by,
                NULL, -- ✅ SAFE: NULL session_id prevents conflicts
                NOW(),
                'COST_UPDATE_DELIVERY',
                'products',
                NEW.product_id,
                JSON_OBJECT(
                    'source', 'trg_update_inventory_costs',
                    'reconciliation_id', NEW.id,
                    'previous_cost', current_product_cost,
                    'delivery_cost', delivery_unit_cost,
                    'new_cost', new_weighted_average_cost,
                    'stock_before', current_stock_quantity,
                    'delivery_qty', NEW.deliveries_received,
                    'cost_change', new_weighted_average_cost - current_product_cost
                ),
                NULL, -- ✅ SAFE: NULL ip_address for trigger context
                NULL, -- ✅ SAFE: NULL user_agent for trigger context
                'Kampala, Uganda',
                CASE
                    WHEN ABS(new_weighted_average_cost - current_product_cost) > (current_product_cost * 0.1) THEN 'HIGH'
                    WHEN ABS(new_weighted_average_cost - current_product_cost) > (current_product_cost * 0.05) THEN 'MEDIUM'
                    ELSE 'LOW'
                END,
                JSON_ARRAY(), -- ✅ SAFE: Empty fraud indicators array
                CONCAT('Automatic cost update via delivery: UGX ',
                       FORMAT(current_product_cost, 2), ' → UGX ',
                       FORMAT(new_weighted_average_cost, 2)),
                'IFRS Inventory Valuation - Automated Weighted Average Cost',
                JSON_OBJECT(
                    'system_context', JSON_OBJECT(
                        'source', 'database_trigger',
                        'trigger_name', 'trg_update_inventory_costs',
                        'database_engine', 'MySQL'
                    ),
                    'request_context', JSON_OBJECT(
                        'method', 'TRIGGER',
                        'source', 'inventory_delivery',
                        'interface_type', 'database_trigger'
                    ),
                    'security_context', JSON_OBJECT(
                        'multi_tenant_verified', true,
                        'station_isolation', true,
                        'automated_process', true
                    )
                ),
                7,
                NOW()
            );
        END IF;

    END IF;
END
$$
