CREATE TRIGGER `trg_tank_volume_calculation` BEFORE INSERT ON `tank_readings` FOR EACH ROW BEGIN
    DECLARE tank_capacity DECIMAL(12,3);
    DECLARE calibration_data JSON;

    -- Get tank specifications
    SELECT total_capacity_litres, calibration_chart
    INTO tank_capacity, calibration_data
    FROM station_tanks
    WHERE id = NEW.tank_id;

    -- Basic volume calculation (can be enhanced with calibration chart)
    IF NEW.calculated_volume_litres = 0.000 THEN
        -- Simple calculation - should be enhanced with actual calibration chart
        SET NEW.calculated_volume_litres = (NEW.fuel_height_mm / 1000) * tank_capacity / 10;
    END IF;

    -- Temperature correction if temperature provided
    IF NEW.temperature_celsius IS NOT NULL THEN
        -- Basic temperature correction (expand based on fuel type)
        SET NEW.corrected_volume_litres = NEW.calculated_volume_litres *
            (1 - ((NEW.temperature_celsius - 15) * 0.001));
    ELSE
        SET NEW.corrected_volume_litres = NEW.calculated_volume_litres;
    END IF;

    -- Validate reading is within tank capacity
    IF NEW.calculated_volume_litres > tank_capacity THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'Calculated volume exceeds tank capacity';
    END IF;
END
$$
